const usersAPI = {
    users: [
        {
            id: 1,
            name: "Fabian Mendez",
            project: "All"
        },
        {
            id: 2,
            name: "Jason Gonzales",
            project: "All"
        },
        {
            id: 3,
            name: "Julio Chevez",
            project: "Capacitacion"
        },
        {
            id: 4,
            name: "Javier Chaparro",
            project: "Kmetrics"
        },
        {
            id: 5,
            name: "Miguel Avendaño",
            project: "Kmetrics"
        }
    ],
    all: function() {
        return this.users;
    },
    get: function(id) {
        return this.users.find(u => u.id === id);
    }
};

export default usersAPI;
